      console.log(`ðŸ”’ Protected path access: /api/super-admin/logout by anonymous`);

      if (req.session.superAdminEmail) {
        console.log(`ðŸ” Super Admin logout: ${req.session.superAdminEmail} at ${new Date().toISOString()}`);
      }

      req.session.destroy((err: any) => {
        if (err) {
          console.error('Session destruction error:', err);
          return res.status(500).json({ message: 'Error during logout' });
        }
        res.json({ message: 'Successfully logged out' });
      });

    } catch (error) {
      console.error('Super admin logout error:', error);
      res.status(500).json({ message: 'Internal server error during logout' });
    }
  });

  // User Church Data API
  app.get('/api/user/church', async (req, res) => {
    try {
      // For now, return the first approved church as demo data
      const approvedChurches = await storage.getApprovedChurches();
      if (approvedChurches.length > 0) {
        res.json(approvedChurches[0]);
      } else {
        res.json({
          id: "demo-church",
          name: "Community Church",
          denomination: "Non-denominational", 
          description: "A vibrant community church serving our local area",
          leadPastor: "Pastor John Smith",
          city: "Johannesburg",
          province: "Gauteng",
          memberCount: 450,
          contactEmail: "info@communitychurch.co.za",
          contactPhone: "+27-11-123-4567",
          servicesTimes: "Sunday Service: 9:00 AM & 6:00 PM",
          status: "approved"
        });
      }
    } catch (error) {
      console.error("Error fetching user church:", error);
      res.status(500).json({ message: "Failed to fetch church data" });
    }
  });

  // User Stats API
  app.get('/api/user/stats', async (req, res) => {
    try {
      const userStats = {
        memberSince: "January 2023",
        totalGiven: "25,480",
        thisYearGiven: "18,750",
        thisMonthGiven: "2,500",
        goalProgress: 75,
        annualGoal: "25,000",
        recentAchievements: ["Faithful Giver", "Community Supporter"],
        transactionCount: 48,
        averageGift: "530",
        upcomingEvents: [
          {
            id: "1",
            title: "Youth Service",
            date: "2025-08-10",
            type: "service"
          },
          {
            id: "2", 
            title: "Community Outreach",
            date: "2025-08-15",
            type: "outreach"
          }
        ]
      };

      res.json(userStats);
    } catch (error) {
      console.error("Error fetching user stats:", error);
      res.status(500).json({ message: "Failed to fetch user statistics" });
    }
  });

  // Community Insights API - Returns real church data
  app.get('/api/church/community-insights', async (req, res) => {
    try {
      // Get authenticated user
      const userId = (req.session as any)?.userId;
      if (!userId) {
        return res.status(401).json({ message: "Not authenticated" });
      }

      // Get user's church
      const user = await storage.getUser(userId);
      if (!user || !user.churchId) {
        return res.status(404).json({ message: "User church not found" });
      }

      // Get church details
      const church = await storage.getChurch(user.churchId);
      if (!church) {
        return res.status(404).json({ message: "Church not found" });
      }

      // Get real church statistics
      const totalMembers = await storage.getChurchMemberCount(user.churchId);

      // Get this month's donation stats for the church
      const currentMonth = new Date();
      const startOfMonth = new Date(currentMonth.getFullYear(), currentMonth.getMonth(), 1);
      const endOfMonth = new Date(currentMonth.getFullYear(), currentMonth.getMonth() + 1, 0);

      const monthlyStats = await storage.getChurchDonationStats(user.churchId, startOfMonth, endOfMonth);

      // Calculate real insights
      const insights = {
        totalMembers: totalMembers,
        activeThisWeek: Math.ceil(totalMembers * 0.2), // Estimate 20% active weekly
        totalDonationsThisMonth: monthlyStats.totalDonations.toFixed(2),
        averageDonation: monthlyStats.transactionCount > 0 ? (monthlyStats.totalDonations / monthlyStats.transactionCount).toFixed(2) : "0.00",
        topContributors: Math.ceil(totalMembers * 0.05), // Estimate 5% are top contributors
        upcomingEvents: 3, // Could be enhanced to count real events
        recentActivities: [
          {
            id: "1",
            type: "member",
            description: `${church.name} has ${totalMembers} active members`,
            timestamp: new Date().toISOString()
          },
          {
            id: "2", 
            type: "donation",
            description: `R${monthlyStats.totalDonations.toFixed(2)} donated this month`,
            timestamp: new Date().toISOString()
          }
        ],
        monthlyGrowth: totalMembers > 100 ? Math.ceil((totalMembers - 100) / 100 * 100) : 5, // Simple growth calculation
        engagementScore: Math.min(95, Math.ceil((monthlyStats.transactionCount / totalMembers) * 100) + 50) // Base 50 + transaction ratio
      };

      console.log(`ðŸ“Š Real community insights for church ${church.name}:`, {
        totalMembers,
        monthlyDonations: monthlyStats.totalDonations,
        transactionCount: monthlyStats.transactionCount
      });

      res.json(insights);
    } catch (error) {
      console.error("Error fetching community insights:", error);
      res.status(500).json({ message: "Failed to fetch community insights" });
    }
  });

  // Object Storage endpoints for document uploads
  app.post("/api/objects/upload", async (req, res) => {
    try {
      const objectStorageService = new ObjectStorageService();
      const uploadURL = await objectStorageService.getObjectEntityUploadURL();
      res.json({ uploadURL });
    } catch (error) {
      console.error("Error creating upload URL:", error);
      res.status(500).json({ error: "Failed to create upload URL" });
    }
  });

  // Endpoint to serve uploaded documents (public access for church verification)
  app.get("/objects/:objectPath(*)", async (req, res) => {
    const objectStorageService = new ObjectStorageService();
    try {
      const objectFile = await objectStorageService.getObjectEntityFile(
        req.path,
      );
      objectStorageService.downloadObject(objectFile, res);
    } catch (error) {
      console.error("Error accessing object:", error);
      if (error instanceof ObjectNotFoundError) {
        return res.sendStatus(404);
      }
      return res.sendStatus(500);
    }
  });

  // Endpoint to update document URLs after upload
  app.put("/api/churches/:churchId/documents", async (req, res) => {
    try {
      const { churchId } = req.params;
      const { documentType, documentUrl } = req.body;

      if (!documentType || !documentUrl) {
        return res.status(400).json({ error: "Document type and URL are required" });
      }

      const objectStorageService = new ObjectStorageService();
      const normalizedPath = objectStorageService.normalizeObjectEntityPath(documentUrl);

      // Update church record with document path
      const updateData: any = {};
      updateData[documentType] = normalizedPath;

      // Note: This is a simplified update - in a real implementation you'd want proper validation
      await storage.updateChurchDocument(churchId, documentType, normalizedPath);

      res.json({ success: true, path: normalizedPath });
    } catch (error) {
      console.error("Error updating church document:", error);
      res.status(500).json({ error: "Failed to update document" });
    }
  });

  // Church Cashback Management APIs
  app.get('/api/super-admin/cashback-records', requireAdminAuth, async (req, res) => {
    try {
      const { churchId, year } = req.query;
      const records = await storage.getChurchCashbackRecords(
        churchId as string, 
        year ? parseInt(year as string) : undefined
      );
      res.json(records);
    } catch (error) {
      console.error("Error fetching cashback records:", error);
      res.status(500).json({ message: "Failed to fetch cashback records" });
    }
  });

  app.post('/api/super-admin/calculate-cashback/:churchId/:year', requireAdminAuth, async (req, res) => {
    try {
      const { churchId, year } = req.params;
      const cashbackRecord = await storage.calculateChurchCashback(churchId, parseInt(year));

      // Log the calculation
      const superAdminId = (req.session as any).superAdminId;
      await storage.logActivity({
        userId: superAdminId,
        churchId: churchId,
        action: 'cashback_calculated',
        entity: 'cashback',
        entityId: cashbackRecord.id,
        details: { 
          year: parseInt(year),
          totalPlatformFees: cashbackRecord.totalPlatformFees,
          cashbackAmount: cashbackRecord.cashbackAmount 
        },
      });

      res.json(cashbackRecord);
    } catch (error) {
      console.error("Error calculating cashback:", error);
      res.status(500).json({ message: "Failed to calculate cashback" });
    }
  });

  app.post('/api/super-admin/process-cashback/:recordId/:action', requireAdminAuth, async (req, res) => {
    try {
      const { recordId, action } = req.params;
      const superAdminId = (req.session as any).superAdminId;

      if (!['approve', 'pay'].includes(action)) {
        return res.status(400).json({ message: "Invalid action. Must be 'approve' or 'pay'" });
      }

      const updatedRecord = await storage.processChurchCashback(recordId, superAdminId, action as 'approve' | 'pay');

      // Log the action
      await storage.logActivity({
        userId: superAdminId,
        churchId: updatedRecord.churchId,
        action: `cashback_${action}`,
        entity: 'cashback',
        entityId: recordId,
        details: { 
          year: updatedRecord.year,
          amount: updatedRecord.cashbackAmount,
          status: updatedRecord.status 
        },
      });

      res.json(updatedRecord);
    } catch (error) {
      console.error("Error processing cashback:", error);
      res.status(500).json({ message: "Failed to process cashback" });
    }
  });

  app.post('/api/super-admin/generate-annual-cashback/:year', requireAdminAuth, async (req, res) => {
    try {
      const { year } = req.params;
      const superAdminId = (req.session as any).superAdminId;

      const cashbackRecords = await storage.generateAnnualCashbackReports(parseInt(year));

      // Calculate totals
      const totalCashback = cashbackRecords.reduce((sum, record) => 
        sum + parseFloat(record.cashbackAmount || '0'), 0
      );

      // Log the bulk generation
      await storage.logActivity({
        userId: superAdminId,
        churchId: null,
        action: 'annual_cashback_generated',
        entity: 'cashback',
        entityId: null,
        details: { 
          year: parseInt(year),
          recordsGenerated: cashbackRecords.length,
          totalCashbackAmount: totalCashback.toString()
        },
      });

      res.json({
        year: parseInt(year),
        recordsGenerated: cashbackRecords.length,
        totalCashbackAmount: totalCashback,
        records: cashbackRecords
      });
    } catch (error) {
      console.error("Error generating annual cashback:", error);
      res.status(500).json({ message: "Failed to generate annual cashback reports" });
    }
  });

  const httpServer = createServer(app);
  return httpServer;
}

server/storage.ts.content:
import {
  users,
  churches,
  projects,
  transactions,
  payouts,
  activityLogs,
  churchCashbackRecords,
  wallets,
  walletTransactions,
  walletTopUpMethods,
  payfastTransactions,
  paymentMethods,
  donations,
  type User,
  type UpsertUser,
  type Church,
  type InsertChurch,
  type Project,
  type InsertProject,
  type Transaction,
  type InsertTransaction,
  type Payout,
  type InsertPayout,
  type ActivityLog,
  type InsertActivityLog,
  type ChurchCashbackRecord,
  type InsertChurchCashbackRecord,
  type Wallet,
  type InsertWallet,
  type WalletTransaction,
  type InsertWalletTransaction,
  type WalletTopUpMethod,
  type InsertWalletTopUpMethod,
  type PayfastTransaction,
  type InsertPayfastTransaction,
  type PaymentMethod,
  type InsertPaymentMethod,
  type Donation,
  type InsertDonation,
  admins,
  superAdmins,
  type Admin,
  type InsertAdmin,
  type SuperAdmin,
  type InsertSuperAdmin,
} from "@shared/schema";
import { db } from "./db";
import { eq, and, desc, asc, count, sql, or, ilike, gte, lte, ne, isNotNull, sum } from "drizzle-orm";
import { randomUUID } from "crypto";

export interface IStorage {
  // User operations (required for Replit Auth)
  getUser(id: string): Promise<User | undefined>;
  getUserById(id: string): Promise<User | undefined>;
  getUserByEmail(email: string): Promise<User | undefined>;
  upsertUser(user: UpsertUser): Promise<User>;
  updateUserRole(userId: string, role: string, churchId?: string): Promise<User>;

  // Church operations
  createChurch(church: InsertChurch): Promise<Church>;
  getChurch(id: string): Promise<Church | undefined>;
  getChurchByAdminId(adminId: string): Promise<Church | undefined>;
  getChurchByPasswordToken(token: string): Promise<Church | undefined>;
  updateChurch(id: string, updates: Partial<Church>): Promise<Church>;
  updateChurchStatus(id: string, status: string, processedBy?: string): Promise<Church>;
  updateChurchDocument(churchId: string, documentType: string, documentPath: string): Promise<Church>;
  getAllChurches(limit?: number, offset?: number): Promise<Church[]>;
  getPendingChurches(): Promise<Church[]>;
  getApprovedChurches(): Promise<Church[]>;
  getChurchStats(): Promise<{ total: number; pending: number; approved: number; active: number }>;
  createChurchAdmin(adminData: { email: string; firstName: string; lastName: string; password: string; churchId: string; role: string }): Promise<User>;

  // Project operations
  createProject(project: InsertProject): Promise<Project>;
  getProject(id: string): Promise<Project | undefined>;
  getChurchProjects(churchId: string): Promise<Project[]>;
  getPublicProjects(limit?: number): Promise<Project[]>;
  updateProjectAmount(id: string, amount: number): Promise<Project>;

  // Transaction operations
  createTransaction(transaction: InsertTransaction): Promise<Transaction>;
  getTransaction(id: string): Promise<Transaction | undefined>;
  getChurchTransactions(churchId: string, limit?: number): Promise<Transaction[]>;
  getUserTransactions(userId: string, limit?: number): Promise<Transaction[]>;
  getTransactionStats(churchId?: string): Promise<any>;

  // Payout operations
  createPayout(payout: InsertPayout): Promise<Payout>;
  getPayout(id: string): Promise<Payout | undefined>;
  getChurchPayouts(churchId: string): Promise<Payout[]>;
  getAllPayouts(status?: string): Promise<Payout[]>;
  getPayoutById(id: string): Promise<Payout | undefined>;
  updatePayoutStatus(id: string, status: string, processedBy?: string, rejectionReason?: string): Promise<Payout>;
  updatePayoutReference(payoutId: string, paymentReference: string): Promise<void>;

  // Activity logging
  logActivity(activity: InsertActivityLog): Promise<ActivityLog>;
  getActivityLogs(userId?: string, churchId?: string, limit?: number): Promise<ActivityLog[]>;

  // Dashboard and analytics
  getPlatformStats(): Promise<any>;
  getChurchDashboardData(churchId: string): Promise<any>;
  getMemberDashboardData(userId: string): Promise<any>;
  getSuperAdminAnalytics(): Promise<any>;

  // Wallet operations
  createWallet(wallet: InsertWallet): Promise<Wallet>;
  getWallet(id: string): Promise<Wallet | undefined>;
  getUserWallet(userId: string): Promise<Wallet | undefined>;
  updateWalletBalance(walletId: string, availableBalance: number, pendingBalance?: number): Promise<Wallet>;

  // Wallet transactions
  createWalletTransaction(transaction: InsertWalletTransaction): Promise<WalletTransaction>;
  getWalletTransaction(id: string): Promise<WalletTransaction | undefined>;
  getWalletTransactions(walletId: string, limit?: number, offset?: number): Promise<WalletTransaction[]>;
  getUserWalletTransactions(userId: string, limit?: number, offset?: number): Promise<WalletTransaction[]>;
  updateWalletTransactionStatus(id: string, status: string, failureReason?: string): Promise<WalletTransaction>;

  // Member search and transfers
  searchMembers(query: string, excludeUserId?: string): Promise<User[]>;
  processWalletTransfer(fromUserId: string, toUserId: string, amount: number, description?: string): Promise<{ success: boolean; transactionId?: string; error?: string }>;

  // PayFast integration
  createPayfastTransaction(transaction: InsertPayfastTransaction): Promise<PayfastTransaction>;
  updatePayfastTransaction(id: string, updates: Partial<PayfastTransaction>): Promise<PayfastTransaction>;

  // Additional methods for API endpoints
  getAllTransactions(): Promise<Transaction[]>;

  // Church branding and member personalization
  getUserChurch(userId: string): Promise<Church | undefined>;
  getUserStats(userId: string): Promise<any>;

  // Admin operations
  createAdmin(admin: InsertAdmin): Promise<Admin>;
  getAdminByEmail(email: string): Promise<Admin | undefined>;
  getAdminById(id: string): Promise<Admin | undefined>;
  updateAdminLogin(adminId: string): Promise<Admin>;
  incrementFailedLoginAttempts(adminId: string): Promise<Admin>;
  updateAdminBackupCodes(adminId: string, backupCodes: string[]): Promise<Admin>;
  enableTwoFactor(adminId: string): Promise<Admin>;
  disableTwoFactor(adminId: string): Promise<Admin>;

  // Super Admin operations (IMPORTANT) - for super admin auth system
  createSuperAdmin(superAdmin: InsertSuperAdmin): Promise<SuperAdmin>;
  getSuperAdminByEmail(email: string): Promise<SuperAdmin | undefined>;
  getSuperAdminById(id: string): Promise<SuperAdmin | undefined>;
  updateSuperAdminLoginInfo(id: string, loginInfo: Partial<Pick<SuperAdmin, 'lastLoginAt' | 'failedLoginAttempts' | 'accountLockedUntil'>>): Promise<SuperAdmin>;
  updateSuperAdminTwoFactor(id: string, twoFactorData: Partial<Pick<SuperAdmin, 'twoFactorSecret' | 'twoFactorEnabled' | 'twoFactorBackupCodes'>>): Promise<SuperAdmin>;
  deleteSuperAdminByEmail(email: string): Promise<void>;

  // Church cashback operations for annual 10% revenue sharing
  calculateChurchCashback(churchId: string, year: number): Promise<ChurchCashbackRecord>;
  getChurchCashbackRecords(churchId?: string, year?: number): Promise<ChurchCashbackRecord[]>;
  processChurchCashback(recordId: string, adminId: string, action: 'approve' | 'pay'): Promise<ChurchCashbackRecord>;
  generateAnnualCashbackReports(year: number): Promise<ChurchCashbackRecord[]>;

  // Church multi-step registration operations
  getChurchByEmail(email: string): Promise<any>;
  createChurchStepOne(data: { email: string; password: string }): Promise<string>;
  authenticateChurch(email: string, password: string): Promise<any>;
}

export class DatabaseStorage implements IStorage {
  // User operations
  async getUser(id: string): Promise<User | undefined> {
    const [user] = await db.select().from(users).where(eq(users.id, id));
    return user;
  }

  async getUserById(id: string): Promise<User | undefined> {
    return await this.getUser(id);
  }

  async getUserByEmail(email: string): Promise<User | undefined> {
    const [user] = await db.select().from(users).where(eq(users.email, email));
    return user;
  }

  async upsertUser(userData: UpsertUser): Promise<User> {
    const [user] = await db
      .insert(users)
      .values(userData)
      .onConflictDoUpdate({
        target: users.id,
        set: {
          ...userData,
          updatedAt: new Date(),
        },
      })
      .returning();
    return user;
  }

  async updateUserRole(userId: string, role: string, churchId?: string): Promise<User> {
    const [user] = await db
      .update(users)
      .set({ role: role as any, churchId, updatedAt: new Date() })
      .where(eq(users.id, userId))
      .returning();
    return user;
  }

  // Church operations
  async createChurch(church: any): Promise<Church> {
    const [newChurch] = await db.insert(churches).values({
      ...church,
      logoUrl: church.logoUrl || church.logo, // Handle both logoUrl and logo fields from registration
    }).returning();
    return newChurch;
  }

  // Project operations for sponsored projects
  async getSponsoredProjects(limit: number = 6): Promise<any[]> {
    try {
      const sponsoredProjects = await db.select({
        id: projects.id,
        name: projects.name,
        description: projects.description,
        targetAmount: projects.targetAmount,
        currentAmount: projects.currentAmount,
        imageUrl: projects.imageUrl,
        endDate: projects.endDate,
        priority: projects.priority,
        churchId: projects.churchId,
        churchName: churches.name,
        createdAt: projects.createdAt
      })
      .from(projects)
      .innerJoin(churches, eq(projects.churchId, churches.id))
      .where(and(
        eq(projects.isSponsored, true),
        eq(projects.status, 'active'),
        eq(churches.status, 'approved')
      ))
      .orderBy(desc(projects.priority), desc(projects.createdAt))
      .limit(limit);

      return sponsoredProjects;
    } catch (error) {
      console.error('Error fetching sponsored projects:', error);
      return [];
    }
  }

  async getChurch(id: string): Promise<Church | undefined> {
    const [church] = await db.select().from(churches).where(eq(churches.id, id));
    return church;
  }

  async getChurchById(id: string): Promise<Church | undefined> {
    const [church] = await db.select().from(churches).where(eq(churches.id, id));
    return church;
  }

  async getChurchByAdminId(adminId: string): Promise<Church | undefined> {
    const [church] = await db.select().from(churches).where(eq(churches.adminUserId, adminId));
    return church;
  }

  async getChurchByPasswordToken(token: string): Promise<Church | undefined> {
    const [church] = await db.select().from(churches).where(eq(churches.passwordSetupToken, token));
    return church;
  }

  async updateChurch(id: string, updates: Partial<Church>): Promise<Church> {
    // Clean the updates object to only include valid church fields
    const validFields = [
      'name', 'denomination', 'registrationNumber', 'taxNumber', 'yearEstablished', 
      'contactEmail', 'contactPhone', 'alternativePhone', 'website',
      'address', 'city', 'province', 'postalCode', 'country',
      'bankName', 'accountNumber', 'branchCode', 'accountHolder', 'accountType',
      'description', 'memberCount', 'servicesTimes', 'leadPastor', 'logoUrl',
      'adminFirstName', 'adminLastName', 'adminEmail', 'adminPhone', 'adminPosition',
      'hasNpoRegistration', 'hasTaxClearance', 'hasBankConfirmation',
      'status', 'isActive'
    ];

    const cleanUpdates: any = {};
    for (const [key, value] of Object.entries(updates)) {
      if (validFields.includes(key) && value !== undefined) {
        // Convert yearEstablished to string if it's a number
        if (key === 'yearEstablished' && typeof value === 'number') {
          cleanUpdates[key] = value.toString();
        } else {
          cleanUpdates[key] = value;
        }
      }
    }

    cleanUpdates.updatedAt = new Date();

    const [church] = await db
      .update(churches)
      .set(cleanUpdates)
      .where(eq(churches.id, id))
      .returning();
    return church;
  }

  async updateChurchStatus(id: string, status: string, processedBy?: string): Promise<Church> {
    const [church] = await db
      .update(churches)
      .set({ status: status as any, updatedAt: new Date() })
      .where(eq(churches.id, id))
      .returning();
    return church;
  }

  async createChurchAdmin(adminData: { email: string; firstName: string; lastName: string; password: string; churchId: string; role: string }): Promise<User> {
    const [admin] = await db.insert(users).values({
      id: randomUUID(),
      email: adminData.email,
      firstName: adminData.firstName,
      lastName: adminData.lastName,
      password: adminData.password, // This should be hashed
      churchId: adminData.churchId,
      role: adminData.role as any,
      isActive: true,
      createdAt: new Date(),
      updatedAt: new Date()
    }).returning();
    return admin;
  }

  async updateChurchDocument(churchId: string, documentType: string, documentPath: string): Promise<Church> {
    const updateData: any = {};
    updateData[documentType] = documentPath;
    updateData.updatedAt = new Date();

    const [updatedChurch] = await db
      .update(churches)
      .set(updateData)
      .where(eq(churches.id, churchId))
      .returning();

    return updatedChurch;
  }

  async deleteChurch(id: string): Promise<void> {
    await db.delete(churches).where(eq(churches.id, id));
  }

  async getAllChurches(limit = 50, offset = 0): Promise<Church[]> {
    return await db
      .select()
      .from(churches)
      .orderBy(desc(churches.createdAt))
      .limit(limit)
      .offset(offset);
  }

  async getPendingChurches(): Promise<Church[]> {
    return await db
      .select()
      .from(churches)
      .where(eq(churches.status, 'pending'))
      .orderBy(asc(churches.createdAt));
  }

  async getApprovedChurches(): Promise<Church[]> {
    return await db
      .select()
      .from(churches)
      .where(eq(churches.status, 'approved'))
      .orderBy(asc(churches.name));
  }

  async getChurchStats(): Promise<{ total: number; pending: number; approved: number; active: number }> {
    try {
      const stats = await db
        .select({
          total: count(),
          pending: sql<number>`count(*) filter (where status = 'pending')`,
          approved: sql<number>`count(*) filter (where status = 'approved')`,
          active: sql<number>`count(*) filter (where is_active = true)`,
        })
        .from(churches);

      return stats[0] || { total: 0, pending: 0, approved: 0, active: 0 };
    } catch (error) {
      console.error("Error getting church stats:", error);
      return { total: 0, pending: 0, approved: 0, active: 0 };
    }
  }

  // Project operations
  async createProject(project: InsertProject): Promise<Project> {
    const [newProject] = await db.insert(projects).values(project).returning();
    return newProject;
  }

  async getProject(id: string): Promise<Project | undefined> {
    const [project] = await db.select().from(projects).where(eq(projects.id, id));
    return project;
  }

