  // Transaction operations
  async createTransaction(transaction: InsertTransaction | any): Promise<Transaction | any> {
    const [newTransaction] = await db.insert(transactions).values(transaction).returning();

    // Update project amount if it's a project donation
    if (newTransaction.projectId && newTransaction.status === 'completed') {
      const project = await this.getProject(newTransaction.projectId);
      if (project) {
        const newAmount = parseFloat(project.currentAmount || '0') + parseFloat(newTransaction.amount || '0');
        await this.updateProjectAmount(newTransaction.projectId, newAmount);
      }
    }

    return newTransaction;
  }

  async processWalletPayment(userId: string, amount: number, type: string): Promise<any> {
    // Get user wallet
    const wallet = await this.getUserWallet(userId);
    if (!wallet || wallet.availableBalance < amount) {
      throw new Error('Insufficient wallet balance');
    }

    // Deduct from wallet
    await this.updateWalletBalance(wallet.id, wallet.availableBalance - amount);

    // Create wallet transaction record
    return await this.createWalletTransaction({
      id: randomUUID(),
      walletId: wallet.id,
      type: 'debit',
      amount: amount.toString(),
      description: `${type} payment`,
      status: 'completed',
      createdAt: new Date(),
      updatedAt: new Date()
    });
  }

