        <div className="flex items-center space-x-3">
          <Button variant="outline" size="sm">
            <Bell className="w-4 h-4 mr-2" />
            Notifications
          </Button>
          <Button size="sm" className="bg-churpay-gradient text-white">
            <Settings className="w-4 h-4 mr-2" />
            Settings
          </Button>
        </div>
