              value={mockOverviewStats.upcomingEvents}
              subtitle="This month"
              color="bg-purple-500"
            />
            <StatCard
              icon={Target}
              title="Project Progress"
              value={`${mockOverviewStats.projectCompletion}%`}
              subtitle={`${mockOverviewStats.activeProjects} active`}
              color="bg-orange-500"
            />
