import { useState } from "react";
import { Button } from "@/components/ui/button";
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogDescription } from "@/components/ui/dialog";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Badge } from "@/components/ui/badge";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Textarea } from "@/components/ui/textarea";
import { useQuery } from "@tanstack/react-query";
import { 
  Wallet, 
  Building, 
  CreditCard,
  AlertCircle,
  CheckCircle,
  ArrowRight,
  Banknote,
  Clock
} from "lucide-react";

interface ChurchPayoutModalProps {
  isOpen: boolean;
  onClose: () => void;
  availableBalance: string;
  pendingPayouts: string;
}

interface ChurchProfile {
  bankName: string;
  accountNumber: string;
  branchCode: string;
  accountHolder: string;
  accountType: string;
}

export function ChurchPayoutModal({
  isOpen,
  onClose,
  availableBalance,
  pendingPayouts
}: ChurchPayoutModalProps) {
  const [amount, setAmount] = useState("");
  const [bankAccount, setBankAccount] = useState("");
  const [reason, setReason] = useState("");
  const [isSubmitting, setIsSubmitting] = useState(false);

  // Fetch church profile to get real banking details
  const { data: churchProfile, isLoading: isLoadingProfile } = useQuery<ChurchProfile>({
    queryKey: ['/api/church/profile'],
    enabled: isOpen
  });

  const handleSubmit = async () => {
    setIsSubmitting(true);
    // Simulate API call
    await new Promise(resolve => setTimeout(resolve, 2000));
    setIsSubmitting(false);
    onClose();
  };

  const balance = parseFloat(availableBalance.replace(/,/g, '') || '0');
  const pending = parseFloat(pendingPayouts.replace(/,/g, '') || '0');

  return (
    <Dialog open={isOpen} onOpenChange={onClose}>
      <DialogContent className="sm:max-w-lg">
        <DialogHeader>
          <DialogTitle className="flex items-center space-x-2">
            <Banknote className="h-5 w-5 text-green-600" />
            <span>Request Payout</span>
          </DialogTitle>
          <DialogDescription>
            Request funds to be transferred to your church bank account
          </DialogDescription>
        </DialogHeader>

        <div className="space-y-6">
          {/* Balance Overview */}
          <div className="bg-gradient-to-r from-green-50 to-emerald-50 rounded-xl p-4 border border-green-200">
            <div className="flex items-center justify-between mb-3">
              <h3 className="font-semibold text-green-900">Available Balance</h3>
              <Wallet className="h-5 w-5 text-green-600" />
            </div>
            <div className="grid grid-cols-2 gap-4">
              <div>
                <p className="text-2xl font-bold text-green-900">R{availableBalance}</p>
                <p className="text-sm text-green-600">Ready for payout</p>
              </div>
              <div>
                <p className="text-lg font-semibold text-orange-700">R{pendingPayouts}</p>
                <p className="text-sm text-orange-600">Pending review</p>
              </div>
            </div>
          </div>

          {/* Bank Details Section */}
          {isLoadingProfile ? (
            <div className="bg-gray-50 rounded-lg p-4 border">
              <div className="flex items-center space-x-2 mb-3">
                <CreditCard className="h-5 w-5 text-gray-600" />
                <h4 className="font-medium text-gray-900">Loading Bank Details...</h4>
              </div>
              <div className="animate-pulse">
                <div className="h-4 bg-gray-200 rounded w-1/2 mb-2"></div>
                <div className="h-4 bg-gray-200 rounded w-1/3"></div>
              </div>
            </div>
          ) : churchProfile ? (
            <div className="bg-gray-50 rounded-lg p-4 border">
              <div className="flex items-center space-x-2 mb-3">
                <CreditCard className="h-5 w-5 text-gray-600" />
                <h4 className="font-medium text-gray-900">Payout Destination</h4>
              </div>
              <div className="grid grid-cols-2 gap-4 text-sm">
                <div>
                  <span className="text-gray-600">Bank:</span>
                  <span className="ml-2 font-medium">{churchProfile.bankName}</span>
                </div>
                <div>
                  <span className="text-gray-600">Account Type:</span>
                  <span className="ml-2 font-medium">{churchProfile.accountType}</span>
                </div>
                <div>
                  <span className="text-gray-600">Account:</span>
                  <span className="ml-2 font-medium">***{churchProfile.accountNumber.slice(-4)}</span>
                </div>
                <div>
                  <span className="text-gray-600">Branch:</span>
                  <span className="ml-2 font-medium">{churchProfile.branchCode}</span>
                </div>
                <div className="col-span-2">
                  <span className="text-gray-600">Account Holder:</span>
                  <span className="ml-2 font-medium">{churchProfile.accountHolder}</span>
                </div>
              </div>
            </div>
          ) : (
            <div className="bg-red-50 rounded-lg p-4 border border-red-200">
              <div className="flex items-center space-x-2 mb-3">
                <AlertCircle className="h-5 w-5 text-red-600" />
                <h4 className="font-medium text-red-900">Banking Details Required</h4>
              </div>
              <p className="text-sm text-red-700">
                Please complete your church profile with banking details before requesting payouts.
              </p>
            </div>
          )}

          {/* Payout Form */}
          <div className="space-y-4">
            <div>
              <Label htmlFor="amount">Payout Amount</Label>
              <div className="relative mt-1">
                <span className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-500">R</span>
                <Input
                  id="amount"
                  type="number"
                  placeholder="0.00"
                  value={amount}
                  onChange={(e) => setAmount(e.target.value)}
                  className="pl-8"
                  max={balance}
                />
              </div>
              {parseFloat(amount) > balance && (
                <p className="text-sm text-red-600 mt-1 flex items-center">
                  <AlertCircle className="h-4 w-4 mr-1" />
                  Amount exceeds available balance
                </p>
              )}
            </div>

            <div>
              <Label htmlFor="reason">Reason for Payout</Label>
              <Textarea
                id="reason"
                placeholder="Brief description of how funds will be used..."
                value={reason}
                onChange={(e) => setReason(e.target.value)}
                className="mt-1"
                rows={3}
              />
            </div>
          </div>

          {/* Processing Info */}
          <div className="bg-blue-50 rounded-lg p-4 border border-blue-200">
            <div className="flex items-start space-x-3">
              <Clock className="h-5 w-5 text-blue-600 mt-0.5" />
              <div>
                <h4 className="font-medium text-blue-900">Processing Timeline</h4>
                <p className="text-sm text-blue-700 mt-1">
                  Payout requests are reviewed within 1-2 business days. Approved funds are 
                  transferred within 3-5 business days.
                </p>
              </div>
            </div>
          </div>

          {/* Actions */}
          <div className="flex space-x-3">
            <Button
              variant="outline"
              onClick={onClose}
              className="flex-1"
              disabled={isSubmitting}
            >
              Cancel
            </Button>
            <Button
              onClick={handleSubmit}
              className="flex-1 bg-green-600 hover:bg-green-700"
              disabled={isSubmitting || !amount || !churchProfile || parseFloat(amount) > balance}
            >
              {isSubmitting ? (
                <>
                  <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-white mr-2"></div>
                  Processing...
                </>
              ) : (
                <>
                  Request Payout
                  <ArrowRight className="h-4 w-4 ml-2" />
                </>
              )}
            </Button>
          </div>
        </div>
      </DialogContent>
    </Dialog>
  );
}

client/src/components/ChurchProfileModal.content:
import { useState, useEffect } from "react";
import { Button } from "@/components/ui/button";
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogDescription } from "@/components/ui/dialog";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Textarea } from "@/components/ui/textarea";
import { Badge } from "@/components/ui/badge";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { apiRequest } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";
import { 
  Building2, 
  Mail, 
  Phone, 
  MapPin,
  Users,
  Calendar,
  CheckCircle,
  ArrowRight,
  Shield,
  AlertCircle
} from "lucide-react";

interface ChurchProfileModalProps {
  isOpen: boolean;
  onClose: () => void;
}

interface ChurchProfile {
  id: string;
  name: string;
  denomination: string;
  registrationNumber: string;
  taxNumber: string;
  yearEstablished: string;

  // Contact Information
  contactEmail: string;
  contactPhone: string;
  alternativePhone?: string;
  website?: string;

  // Physical Address
  address: string;
  city: string;
  province: string;
  postalCode: string;
  country: string;

  // Banking Information
  bankName: string;
  accountNumber: string;
  branchCode: string;
  accountHolder: string;
  accountType: string;

  // Church Details
  description: string;
  memberCount: number;
  servicesTimes: string;
  leadPastor: string;
  logoUrl?: string;

  // Administrative Contact
  adminFirstName: string;
  adminLastName: string;
  adminEmail: string;
  adminPhone: string;
  adminPosition: string;

  // Status and metadata
  status: string;
  registrationDate: string;
}

export function ChurchProfileModal({
  isOpen,
  onClose
}: ChurchProfileModalProps) {
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const [formData, setFormData] = useState<Partial<ChurchProfile>>({
    name: "",
    denomination: "",
    registrationNumber: "",
    taxNumber: "",
    yearEstablished: "",
    contactEmail: "",
    contactPhone: "",
    alternativePhone: "",
    website: "",
    address: "",
    city: "",
    province: "",
    postalCode: "",
    country: "South Africa",
    bankName: "",
    accountNumber: "",
    branchCode: "",
    accountHolder: "",
    accountType: "",
    description: "",
    memberCount: 0,
    servicesTimes: "",
    leadPastor: "",
    logoUrl: "",
    adminFirstName: "",
    adminLastName: "",
    adminEmail: "",
    adminPhone: "",
    adminPosition: ""
  });

  const [imageFile, setImageFile] = useState<File | null>(null);
  const [imagePreview, setImagePreview] = useState<string>("");

  // Fetch church profile data
  const { data: churchProfile, isLoading } = useQuery<ChurchProfile>({
    queryKey: ['/api/church/profile'],
    enabled: isOpen
  });

  // Update form when data loads
  useEffect(() => {
    if (churchProfile) {
      setFormData(churchProfile);
      setImagePreview(churchProfile.logoUrl || "");
    }
  }, [churchProfile]);

  // Handle image file selection
  const handleImageChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    const file = event.target.files?.[0];

    if (file) {
      // Validate file type
      if (!file.type.startsWith('image/')) {
        toast({
          title: "Invalid File",
          description: "Please select an image file.",
          variant: "destructive",
        });
        return;
      }

      // Validate file size (max 5MB)
      if (file.size > 5 * 1024 * 1024) {
        toast({
          title: "File Too Large",
          description: "Please select an image smaller than 5MB.",
          variant: "destructive",
        });
        return;
      }

      setImageFile(file);

      // Create preview
      const reader = new FileReader();
      reader.onload = (e) => {
        setImagePreview(e.target?.result as string);
      };
      reader.readAsDataURL(file);
    }
  };

  // Remove image
  const handleRemoveImage = () => {
    setImageFile(null);
    setImagePreview("");
    setFormData(prev => ({ ...prev, logoUrl: "" }));
  };

  // Update church profile mutation
  const updateProfileMutation = useMutation({
    mutationFn: async (data: Partial<ChurchProfile>) => {
      const response = await fetch('/api/church/profile', {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(data)
      });

      if (!response.ok) {
        throw new Error('Failed to update profile');
      }

      return response.json();
    },
    onSuccess: () => {
      toast({
        title: "Profile Updated",
        description: "Church profile has been successfully updated.",
      });
      queryClient.invalidateQueries({ queryKey: ['/api/church/profile'] });
      queryClient.invalidateQueries({ queryKey: ['/api/church/stats'] });
      onClose();
    },
    onError: (error: Error) => {
      toast({
        title: "Update Failed",
        description: error.message || "Failed to update church profile.",
        variant: "destructive",
      });
    }
  });

  const handleInputChange = (field: keyof ChurchProfile, value: string | number) => {
    setFormData(prev => ({ ...prev, [field]: value }));
  };

  const handleSubmit = async () => {
    if (!formData.name || !formData.contactEmail || !formData.contactPhone) {
      toast({
        title: "Validation Error",
        description: "Please fill in all required fields.",
        variant: "destructive",
      });
      return;
    }

    let profileData = { ...formData };

    // If there's a new image file, convert to base64 for submission
    if (imageFile) {
      const reader = new FileReader();
      reader.onload = () => {
        profileData.logoUrl = reader.result as string;
        updateProfileMutation.mutate(profileData);
      };
      reader.readAsDataURL(imageFile);
    } else {
      // If preview exists but no new file, keep existing URL
      if (imagePreview && !imageFile) {
        profileData.logoUrl = formData.logoUrl;
      }
      updateProfileMutation.mutate(profileData);
    }
  };

  if (isLoading) {
    return (
      <Dialog open={isOpen} onOpenChange={onClose}>
        <DialogContent className="sm:max-w-2xl">
          <div className="flex items-center justify-center p-8">
            <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-purple-600"></div>
            <span className="ml-3 text-gray-600">Loading church profile...</span>
          </div>
        </DialogContent>
      </Dialog>
    );
  }

  return (
    <Dialog open={isOpen} onOpenChange={onClose}>
      <DialogContent className="sm:max-w-4xl max-h-[90vh] overflow-y-auto">
        <DialogHeader>
          <DialogTitle className="flex items-center space-x-2">
            <Building2 className="h-5 w-5 text-purple-600" />
            <span>Church Profile Settings</span>
          </DialogTitle>
          <DialogDescription>
            Update your church information and banking details
          </DialogDescription>
        </DialogHeader>

        <div className="space-y-8">
          {/* Profile Picture Section */}
          <div className="flex items-center space-x-6">
            <div className="relative">
              <div className="w-24 h-24 rounded-full overflow-hidden bg-gradient-to-br from-purple-100 to-indigo-100 flex items-center justify-center border-4 border-white shadow-lg">
                {imagePreview ? (
                  <img 
                    src={imagePreview} 
                    alt="Church profile" 
                    className="w-full h-full object-cover"
                  />
                ) : (
                  <Building2 className="h-10 w-10 text-purple-600" />
                )}
              </div>
              {imagePreview && (
                <Button
                  type="button"
                  variant="destructive"
                  size="sm"
                  className="absolute -top-2 -right-2 h-6 w-6 rounded-full p-0"
                  onClick={handleRemoveImage}
                >
                  Ã—
                </Button>
              )}
            </div>

            <div className="flex-1">
              <Label htmlFor="profileImage" className="block text-sm font-medium text-gray-700 mb-2">
                Church Profile Picture
              </Label>
              <input
                id="profileImage"
                type="file"
                accept="image/*"
                onChange={handleImageChange}
                className="hidden"
              />
              <div className="flex space-x-3">
                <Button
                  type="button"
                  variant="outline"
                  onClick={() => document.getElementById('profileImage')?.click()}
                  className="flex items-center space-x-2"
                >
                  <Building2 className="h-4 w-4" />
                  <span>Choose Image</span>
                </Button>
                <div className="text-sm text-gray-500 flex items-center">
                  JPG, PNG or GIF (max 5MB)
                </div>
              </div>
            </div>
          </div>

          {/* Status Badge */}
          <div className="flex items-center justify-between">
            <div className="flex items-center space-x-3">
              <Badge 
                variant={formData.status === 'approved' ? 'default' : 'secondary'}
                className={formData.status === 'approved' ? 'bg-green-100 text-green-800' : ''}
              >
                <Shield className="h-3 w-3 mr-1" />
                {formData.status === 'approved' ? 'Verified Church' : 'Pending Verification'}
              </Badge>
              <span className="text-sm text-gray-500">
                Member since {new Date(formData.registrationDate || '').toLocaleDateString()}
              </span>
            </div>
          </div>

          {/* Church Information */}
          <div className="space-y-6">
            <h3 className="text-lg font-semibold text-gray-900 border-b pb-2">Church Information</h3>

            <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
              <div>
                <Label htmlFor="name">Church Name *</Label>
                <Input
                  id="name"
                  value={formData.name || ''}
                  onChange={(e) => handleInputChange('name', e.target.value)}
                  placeholder="Enter church name"
                  className="mt-1"
                />
              </div>
              <div>
                <Label htmlFor="denomination">Denomination *</Label>
                <Select 
                  value={formData.denomination || ''} 
                  onValueChange={(value) => handleInputChange('denomination', value)}
                >
                  <SelectTrigger className="mt-1">
                    <SelectValue placeholder="Select denomination" />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="anglican">Anglican</SelectItem>
                    <SelectItem value="baptist">Baptist</SelectItem>
                    <SelectItem value="methodist">Methodist</SelectItem>
                    <SelectItem value="presbyterian">Presbyterian</SelectItem>
                    <SelectItem value="pentecostal">Pentecostal</SelectItem>
                    <SelectItem value="catholic">Catholic</SelectItem>
                    <SelectItem value="reformed">Reformed</SelectItem>
                    <SelectItem value="apostolic">Apostolic</SelectItem>
                    <SelectItem value="evangelical">Evangelical</SelectItem>
                    <SelectItem value="other">Other</SelectItem>
                  </SelectContent>
                </Select>
              </div>
            </div>

            <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
              <div>
                <Label htmlFor="registrationNumber">Registration Number</Label>
                <Input
                  id="registrationNumber"
                  value={formData.registrationNumber || ''}
                  onChange={(e) => handleInputChange('registrationNumber', e.target.value)}
                  placeholder="NPO/PBO Registration Number"
                  className="mt-1"
                />
              </div>
              <div>
                <Label htmlFor="taxNumber">Tax Number</Label>
                <Input
                  id="taxNumber"
                  value={formData.taxNumber || ''}
                  onChange={(e) => handleInputChange('taxNumber', e.target.value)}
                  placeholder="Tax Exemption Number"
                  className="mt-1"
                />
              </div>
            </div>

            <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
              <div>
                <Label htmlFor="memberCount">Current Member Count</Label>
                <Input
                  id="memberCount"
                  type="number"
                  value={formData.memberCount || ''}
                  onChange={(e) => handleInputChange('memberCount', parseInt(e.target.value) || 0)}
                  placeholder="Enter member count"
                  className="mt-1"
                />
              </div>
              <div>
                <Label htmlFor="yearEstablished">Year Established</Label>
                <Input
                  id="yearEstablished"
                  value={formData.yearEstablished || ''}
                  onChange={(e) => handleInputChange('yearEstablished', e.target.value)}
                  placeholder="YYYY"
                  className="mt-1"
                />
              </div>
            </div>

            <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
              <div>
                <Label htmlFor="leadPastor">Lead Pastor</Label>
                <Input
                  id="leadPastor"
                  value={formData.leadPastor || ''}
                  onChange={(e) => handleInputChange('leadPastor', e.target.value)}
                  placeholder="Pastor Name"
                  className="mt-1"
                />
              </div>
              <div>
                <Label htmlFor="servicesTimes">Service Times</Label>
                <Input
                  id="servicesTimes"
                  value={formData.servicesTimes || ''}
                  onChange={(e) => handleInputChange('servicesTimes', e.target.value)}
                  placeholder="Sunday 9AM, 11AM; Wednesday 7PM"
                  className="mt-1"
                />
              </div>
            </div>

            <div>
              <Label htmlFor="description">Church Description</Label>
              <Textarea
                id="description"
                value={formData.description || ''}
                onChange={(e) => handleInputChange('description', e.target.value)}
                placeholder="Brief description of your church mission and values..."
                className="mt-1"
                rows={3}
              />
            </div>
          </div>

          {/* Contact Information */}
          <div className="space-y-6">
            <h3 className="text-lg font-semibold text-gray-900 border-b pb-2">Contact Information</h3>

            <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
              <div>
                <Label htmlFor="contactEmail">Primary Email *</Label>
                <div className="relative mt-1">
                  <Mail className="absolute left-3 top-1/2 transform -translate-y-1/2 h-4 w-4 text-gray-400" />
                  <Input
                    id="contactEmail"
                    type="email"
                    value={formData.contactEmail || ''}
                    onChange={(e) => handleInputChange('contactEmail', e.target.value)}
                    placeholder="church@email.com"
                    className="pl-10"
                  />
                </div>
              </div>
              <div>
                <Label htmlFor="contactPhone">Primary Phone *</Label>
                <div className="relative mt-1">
                  <Phone className="absolute left-3 top-1/2 transform -translate-y-1/2 h-4 w-4 text-gray-400" />
                  <Input
                    id="contactPhone"
                    value={formData.contactPhone || ''}
                    onChange={(e) => handleInputChange('contactPhone', e.target.value)}
                    placeholder="+27 21 555 0123"
                    className="pl-10"
                  />
                </div>
              </div>
            </div>

            <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
              <div>
                <Label htmlFor="alternatePhone">Alternate Phone</Label>
                <Input
                  id="alternatePhone"
                  value={formData.alternativePhone || ''}
                  onChange={(e) => handleInputChange('alternativePhone', e.target.value)}
                  placeholder="+27 82 555 0123"
                  className="mt-1"
                />
              </div>
              <div>
                <Label htmlFor="website">Website</Label>
                <Input
                  id="website"
                  value={formData.website || ''}
                  onChange={(e) => handleInputChange('website', e.target.value)}
                  placeholder="https://www.church.co.za"
                  className="mt-1"
                />
              </div>
            </div>
          </div>

          {/* Address Information */}
          <div className="space-y-6">
            <h3 className="text-lg font-semibold text-gray-900 border-b pb-2">Physical Address</h3>

            <div>
              <Label htmlFor="address">Street Address *</Label>
              <div className="relative mt-1">
                <MapPin className="absolute left-3 top-1/2 transform -translate-y-1/2 h-4 w-4 text-gray-400" />
                <Input
                  id="address"
                  value={formData.address || ''}
                  onChange={(e) => handleInputChange('address', e.target.value)}
                  placeholder="123 Church Street"
                  className="pl-10"
                />
              </div>
            </div>

            <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
              <div>
                <Label htmlFor="city">City *</Label>
                <Input
                  id="city"
                  value={formData.city || ''}
                  onChange={(e) => handleInputChange('city', e.target.value)}
                  placeholder="Cape Town"
                  className="mt-1"
                />
              </div>
              <div>
                <Label htmlFor="province">Province *</Label>
                <Select 
                  value={formData.province || ''} 
                  onValueChange={(value) => handleInputChange('province', value)}
                >
                  <SelectTrigger className="mt-1">
                    <SelectValue placeholder="Select province" />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="western-cape">Western Cape</SelectItem>
                    <SelectItem value="gauteng">Gauteng</SelectItem>
                    <SelectItem value="kzn">KwaZulu-Natal</SelectItem>
                    <SelectItem value="eastern-cape">Eastern Cape</SelectItem>
                    <SelectItem value="limpopo">Limpopo</SelectItem>
                    <SelectItem value="mpumalanga">Mpumalanga</SelectItem>
                    <SelectItem value="north-west">North West</SelectItem>
                    <SelectItem value="northern-cape">Northern Cape</SelectItem>
                    <SelectItem value="free-state">Free State</SelectItem>
                  </SelectContent>
                </Select>
              </div>
              <div>
                <Label htmlFor="postalCode">Postal Code</Label>
                <Input
                  id="postalCode"
                  value={formData.postalCode || ''}
                  onChange={(e) => handleInputChange('postalCode', e.target.value)}
                  placeholder="8001"
                  className="mt-1"
                />
              </div>
            </div>
          </div>

          {/* Administrative Contact */}
          <div className="space-y-6">
            <h3 className="text-lg font-semibold text-gray-900 border-b pb-2">Administrative Contact</h3>

            <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
              <div>
                <Label htmlFor="adminFirstName">Admin First Name</Label>
                <Input
                  id="adminFirstName"
                  value={formData.adminFirstName || ''}
                  onChange={(e) => handleInputChange('adminFirstName', e.target.value)}
                  placeholder="First Name"
                  className="mt-1"
                />
              </div>
              <div>
                <Label htmlFor="adminLastName">Admin Last Name</Label>
                <Input
                  id="adminLastName"
                  value={formData.adminLastName || ''}
                  onChange={(e) => handleInputChange('adminLastName', e.target.value)}
                  placeholder="Last Name"
                  className="mt-1"
                />
              </div>
            </div>

            <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
              <div>
                <Label htmlFor="adminEmail">Admin Email</Label>
                <Input
                  id="adminEmail"
                  type="email"
                  value={formData.adminEmail || ''}
                  onChange={(e) => handleInputChange('adminEmail', e.target.value)}
                  placeholder="admin@church.co.za"
                  className="mt-1"
                />
              </div>
              <div>
                <Label htmlFor="adminPhone">Admin Phone</Label>
                <Input
                  id="adminPhone"
                  value={formData.adminPhone || ''}
                  onChange={(e) => handleInputChange('adminPhone', e.target.value)}
                  placeholder="+27 21 555 0123"
                  className="mt-1"
                />
              </div>
            </div>

            <div>
              <Label htmlFor="adminPosition">Admin Position</Label>
              <Input
                id="adminPosition"
                value={formData.adminPosition || ''}
                onChange={(e) => handleInputChange('adminPosition', e.target.value)}
                placeholder="Pastor, Church Administrator, etc."
                className="mt-1"
              />
            </div>
          </div>

          {/* Banking Information */}
          <div className="space-y-6">
            <h3 className="text-lg font-semibold text-gray-900 border-b pb-2">Banking Information</h3>

            <div className="bg-amber-50 rounded-lg p-4 border border-amber-200">
              <div className="flex items-start space-x-3">
                <AlertCircle className="h-5 w-5 text-amber-600 mt-0.5" />
                <div>
                  <h4 className="font-medium text-amber-900">Secure Banking Details</h4>
                  <p className="text-sm text-amber-700 mt-1">
                    Your banking information is encrypted and used only for payout processing.
                  </p>
                </div>
              </div>
            </div>

            <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
              <div>
                <Label htmlFor="bankName">Bank Name *</Label>
                <Select 
                  value={formData.bankName || ''} 
                  onValueChange={(value) => handleInputChange('bankName', value)}
                >
                  <SelectTrigger className="mt-1">
                    <SelectValue placeholder="Select bank" />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="absa">ABSA Bank</SelectItem>
                    <SelectItem value="fnb">First National Bank (FNB)</SelectItem>
                    <SelectItem value="standard">Standard Bank</SelectItem>
                    <SelectItem value="nedbank">Nedbank</SelectItem>
                    <SelectItem value="capitec">Capitec Bank</SelectItem>
                    <SelectItem value="investec">Investec</SelectItem>
                    <SelectItem value="other">Other</SelectItem>
                  </SelectContent>
                </Select>
              </div>
              <div>
                <Label htmlFor="accountType">Account Type *</Label>
                <Select 
                  value={formData.accountType || ''} 
                  onValueChange={(value) => handleInputChange('accountType', value)}
                >
                  <SelectTrigger className="mt-1">
                    <SelectValue placeholder="Select account type" />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="cheque">Cheque Account</SelectItem>
                    <SelectItem value="savings">Savings Account</SelectItem>
                    <SelectItem value="current">Current Account</SelectItem>
                  </SelectContent>
                </Select>
              </div>
            </div>

            <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
              <div>
                <Label htmlFor="accountNumber">Account Number *</Label>
                <Input
                  id="accountNumber"
                  value={formData.accountNumber || ''}
                  onChange={(e) => handleInputChange('accountNumber', e.target.value)}
                  placeholder="1234567890"
                  className="mt-1"
                />
              </div>
              <div>
                <Label htmlFor="branchCode">Branch Code *</Label>
                <Input
                  id="branchCode"
                  value={formData.branchCode || ''}
                  onChange={(e) => handleInputChange('branchCode', e.target.value)}
                  placeholder="123456"
                  className="mt-1"
                />
              </div>
            </div>

            <div>
              <Label htmlFor="accountHolder">Account Holder Name *</Label>
              <Input
                id="accountHolder"
                value={formData.accountHolder || ''}
                onChange={(e) => handleInputChange('accountHolder', e.target.value)}
                placeholder="Church Name or Authorized Person"
                className="mt-1"
              />
            </div>
          </div>

          {/* Actions */}
          <div className="flex space-x-3 pt-6 border-t">
            <Button
              variant="outline"
              onClick={onClose}
              className="flex-1"
              disabled={updateProfileMutation.isPending}
            >
              Cancel
            </Button>
            <Button
              onClick={handleSubmit}
              className="flex-1 bg-purple-600 hover:bg-purple-700"
              disabled={updateProfileMutation.isPending}
            >
              {updateProfileMutation.isPending ? (
                <>
                  <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-white mr-2"></div>
                  Updating Profile...
                </>
              ) : (
                <>
                  Update Profile
                  <CheckCircle className="h-4 w-4 ml-2" />
                </>
              )}
            </Button>
          </div>
        </div>
      </DialogContent>
    </Dialog>
  );
}

