import { useState } from "react";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Dialog, DialogContent, DialogHeader, DialogTitle } from "@/components/ui/dialog";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Badge } from "@/components/ui/badge";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { Separator } from "@/components/ui/separator";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { 
  Wallet, 
  ArrowUp, 
  ArrowDown, 
  Send, 
  History, 
  CreditCard,
  Plus,
  Eye,
  EyeOff,
  Star,
  Church,
  Heart,
  Shield,
  CheckCircle
} from "lucide-react";
import { WalletTransaction } from "@shared/schema";
import { useMutation } from "@tanstack/react-query";
import { apiRequest } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";

interface WalletModalProps {
  isOpen: boolean;
  onClose: () => void;
  walletBalance: number;
  rewardPoints: number;
  transactions: WalletTransaction[];
  onTopUp: () => void;
  onSend: () => void;
}

export function WalletModal({
  isOpen,
  onClose,
  walletBalance,
  rewardPoints,
  transactions,
  onTopUp,
  onSend
}: WalletModalProps) {
  const [showBalance, setShowBalance] = useState(true);
  const [sendAmount, setSendAmount] = useState("");
  const [sendRecipient, setSendRecipient] = useState("");
  const [topUpAmount, setTopUpAmount] = useState("");
  const [paymentMethod, setPaymentMethod] = useState("payfast");
  const [activeTab, setActiveTab] = useState("transactions");
  const { toast } = useToast();

  // PayFast wallet top-up mutation
  const topUpMutation = useMutation({
    mutationFn: async (amount: number) => {
      const response = await apiRequest('/api/wallet/topup/payfast', {
        method: 'POST',
        body: JSON.stringify({ amount })
      });
      return response;
    },
    onSuccess: (data) => {
      if (data.paymentUrl) {
        // Redirect to PayFast payment page
        window.open(data.paymentUrl, '_blank');
        toast({
          title: "Redirecting to PayFast",
          description: "Please complete your payment to top up your wallet.",
        });
      }
    },
    onError: (error) => {
      toast({
        title: "Top-up Failed",
        description: "Failed to initiate wallet top-up. Please try again.",
        variant: "destructive",
      });
    },
  });

  // PayFast wallet payment mutation
  const sendMutation = useMutation({
    mutationFn: async ({ amount, recipient }: { amount: number; recipient: string }) => {
      const response = await apiRequest('/api/wallet/send', {
        method: 'POST',
        body: JSON.stringify({ amount, recipient })
      });
      return response;
    },
    onSuccess: () => {
      toast({
        title: "Payment Sent",
        description: "Your payment has been sent successfully.",
      });
      setSendAmount("");
      setSendRecipient("");
    },
    onError: (error) => {
      toast({
        title: "Payment Failed",
        description: "Failed to send payment. Please try again.",
        variant: "destructive",
      });
    },
  });

  const handleTopUp = () => {
    const amount = parseFloat(topUpAmount);
    if (amount < 10) {
      toast({
        title: "Invalid Amount",
        description: "Minimum top-up amount is R10.",
        variant: "destructive",
      });
      return;
    }
    topUpMutation.mutate(amount);
  };

  const handleSendMoney = () => {
    const amount = parseFloat(sendAmount);
    if (amount <= 0 || amount > walletBalance) {
      toast({
        title: "Invalid Amount",
        description: "Please enter a valid amount within your wallet balance.",
        variant: "destructive",
      });
      return;
    }
    if (!sendRecipient) {
      toast({
        title: "Missing Recipient",
        description: "Please enter a recipient email or phone number.",
        variant: "destructive",
      });
      return;
    }
    sendMutation.mutate({ amount, recipient: sendRecipient });
  };

  const formatDate = (date: string | Date) => {
    return new Date(date).toLocaleDateString('en-ZA', {
      month: 'short',
      day: 'numeric',
      hour: '2-digit',
      minute: '2-digit'
    });
  };

  return (
    <Dialog open={isOpen} onOpenChange={onClose}>
      <DialogContent className="sm:max-w-[700px] max-w-[95vw] max-h-[90vh] overflow-y-auto mx-2">
        <DialogHeader>
          <DialogTitle className="flex items-center space-x-2">
            <Wallet className="h-6 w-6 text-purple-600" />
            <span>ChurPay Wallet</span>
          </DialogTitle>
        </DialogHeader>

        <div className="space-y-6">
          {/* Wallet Balance Card */}
          <Card className="bg-gradient-to-br from-purple-600 to-purple-700 border-0 text-white">
            <CardContent className="p-6">
              <div className="flex items-center justify-between">
                <div className="flex items-center space-x-3">
                  <div className="w-12 h-12 bg-white/20 rounded-full flex items-center justify-center">
                    <Wallet className="h-6 w-6 text-white" />
                  </div>
                  <div>
                    <p className="text-sm opacity-90">Available Balance</p>
                    <p className="text-2xl font-bold">
                      {showBalance ? `R ${walletBalance.toLocaleString()}` : "R â€¢â€¢â€¢â€¢â€¢â€¢"}
                    </p>
                  </div>
                </div>
                <div className="flex flex-col items-end space-y-2">
                  <Button
                    variant="ghost"
                    size="sm"
                    onClick={() => setShowBalance(!showBalance)}
                    className="text-white hover:bg-white/20 h-8 w-8 p-0"
                  >
                    {showBalance ? <EyeOff className="h-4 w-4" /> : <Eye className="h-4 w-4" />}
                  </Button>
                  <div className="flex items-center space-x-2">
                    <Star className="h-4 w-4 text-yellow-300" />
                    <span className="text-sm">{rewardPoints} points</span>
                  </div>
                </div>
              </div>
            </CardContent>
          </Card>

          {/* Wallet Actions */}
          <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
            <Button
              onClick={() => setActiveTab("topup")}
              className="bg-green-600 hover:bg-green-700 text-white h-16 rounded-xl flex flex-col items-center justify-center space-y-1"
            >
              <Plus className="h-5 w-5" />
              <span className="text-sm">Top Up via PayFast</span>
            </Button>
            <Button
              onClick={() => setActiveTab("send")}
              className="bg-blue-600 hover:bg-blue-700 text-white h-16 rounded-xl flex flex-col items-center justify-center space-y-1"
            >
              <Send className="h-5 w-5" />
              <span className="text-sm">Send Money</span>
            </Button>
            <Button
              onClick={() => {
                // This would trigger donation modal
                onClose();
              }}
              className="bg-purple-600 hover:bg-purple-700 text-white h-16 rounded-xl flex flex-col items-center justify-center space-y-1"
            >
              <Heart className="h-5 w-5" />
              <span className="text-sm">Donate</span>
            </Button>
          </div>

          {/* Wallet Features Tabs */}
          <Tabs value={activeTab} onValueChange={setActiveTab} className="w-full">
            <TabsList className="grid w-full grid-cols-5">
              <TabsTrigger value="transactions">Transactions</TabsTrigger>
              <TabsTrigger value="topup">Top Up</TabsTrigger>
              <TabsTrigger value="send">Send</TabsTrigger>
              <TabsTrigger value="rewards">Rewards</TabsTrigger>
              <TabsTrigger value="settings">Settings</TabsTrigger>
            </TabsList>

            <TabsContent value="transactions" className="space-y-4">
              <div className="flex items-center justify-between">
                <h3 className="text-lg font-semibold">Recent Transactions</h3>
                <Button variant="outline" size="sm">
                  <History className="h-4 w-4 mr-2" />
