                    <p className="text-gray-500">No transactions yet</p>
                    <p className="text-gray-400 text-sm">Start using your wallet to see transaction history</p>
                  </div>
                )}
              </div>
            </TabsContent>

            <TabsContent value="topup" className="space-y-4">
              <Card>
                <CardHeader>
                  <CardTitle className="flex items-center space-x-2">
                    <CreditCard className="h-5 w-5 text-green-600" />
                    <span>Top Up Wallet with PayFast</span>
                  </CardTitle>
                </CardHeader>
                <CardContent className="space-y-4">
                  <div className="flex items-center space-x-2 p-3 bg-green-50 rounded-lg border border-green-200">
                    <Shield className="h-5 w-5 text-green-600" />
                    <span className="text-sm text-green-700">Secure payments powered by PayFast</span>
                  </div>

                  <div className="space-y-4">
                    <div>
                      <Label htmlFor="topup-amount">Top-up Amount (Minimum R10)</Label>
                      <Input
                        id="topup-amount"
                        type="number"
                        placeholder="0.00"
                        value={topUpAmount}
                        onChange={(e) => setTopUpAmount(e.target.value)}
                        min="10"
                        step="0.01"
                      />
                    </div>

                    <div className="grid grid-cols-4 gap-2">
                      {[50, 100, 200, 500].map((amount) => (
                        <Button
                          key={amount}
                          variant="outline"
                          size="sm"
                          onClick={() => setTopUpAmount(amount.toString())}
                          className="h-10"
                        >
                          R{amount}
