client/src/hooks/useAdminAuth.ts.content:
import { useState, useEffect } from 'react';
import { apiRequest } from '@/lib/queryClient';

interface Admin {
  id: string;
  firstName: string;
  lastName: string;
  email: string;
  role: string;
  isActive: boolean;
  lastLoginAt: string | null;
  createdAt: string;
}

interface AdminAuthState {
  admin: Admin | null;
  token: string | null;
  isAuthenticated: boolean;
  isLoading: boolean;
}

export function useAdminAuth() {
  const [authState, setAuthState] = useState<AdminAuthState>({
    admin: null,
    token: null,
    isAuthenticated: false,
    isLoading: true
  });

  useEffect(() => {
    checkAuthStatus();
  }, []);

  const checkAuthStatus = async () => {
    try {
      const storedAuth = localStorage.getItem('adminAuth');
      if (!storedAuth) {
        setAuthState(prev => ({ ...prev, isLoading: false }));
        return;
      }

      const parsedAuth = JSON.parse(storedAuth);
      if (!parsedAuth.token || !parsedAuth.admin) {
        localStorage.removeItem('adminAuth');
        setAuthState(prev => ({ ...prev, isLoading: false }));
        return;
      }

      // Verify token is still valid by making an API call
      const response = await fetch('/api/admin/profile', {
        headers: {
          'Authorization': `Bearer ${parsedAuth.token}`
        }
      });

      if (response.ok) {
        const admin = await response.json();
        setAuthState({
          admin,
          token: parsedAuth.token,
          isAuthenticated: true,
          isLoading: false
        });
      } else {
        // Token is invalid, clear storage
        localStorage.removeItem('adminAuth');
        setAuthState({
          admin: null,
          token: null,
          isAuthenticated: false,
          isLoading: false
        });
      }
    } catch (error) {
      console.error('Error checking admin auth status:', error);
      localStorage.removeItem('adminAuth');
      setAuthState({
        admin: null,
        token: null,
        isAuthenticated: false,
        isLoading: false
      });
    }
  };

  const signIn = async (email: string, password: string, rememberMe = false) => {
    try {
      const response = await apiRequest('POST', '/api/admin/signin', {
        email,
        password,
        rememberMe
      });

      const authData = await response.json();

      // Store auth data
      localStorage.setItem('adminAuth', JSON.stringify({
        admin: authData.admin,
        token: authData.token,
        expiresIn: authData.expiresIn
      }));

      setAuthState({
        admin: authData.admin,
        token: authData.token,
        isAuthenticated: true,
        isLoading: false
      });

      return { success: true, data: authData };
    } catch (error: any) {
      return { 
        success: false, 
        error: error.message || 'Sign in failed' 
      };
    }
  };

  const signOut = async () => {
    try {
      if (authState.token) {
        await fetch('/api/admin/logout', {
          method: 'POST',
          headers: {
            'Authorization': `Bearer ${authState.token}`
          }
        });
      }
    } catch (error) {
      console.error('Error during admin logout:', error);
    } finally {
      localStorage.removeItem('adminAuth');
      setAuthState({
        admin: null,
        token: null,
        isAuthenticated: false,
        isLoading: false
      });
    }
  };

  const makeAuthenticatedRequest = async (method: string, url: string, data?: any) => {
    if (!authState.token) {
      throw new Error('No admin authentication token');
    }

    const response = await fetch(url, {
      method,
      headers: {
        'Content-Type': 'application/json',
        'Authorization': `Bearer ${authState.token}`
      },
      body: data ? JSON.stringify(data) : undefined
    });

    if (response.status === 401) {
      // Token expired, sign out
      await signOut();
      throw new Error('Admin session expired');
    }

    if (!response.ok) {
      const errorData = await response.json().catch(() => ({}));
      throw new Error(errorData.message || `HTTP ${response.status}`);
    }

    return response;
  };

  return {
    ...authState,
    signIn,
    signOut,
    makeAuthenticatedRequest,
    refreshAuth: checkAuthStatus
  };
}

client/src/hooks/useSuperAdminAuth.ts.content:
import { useQuery, useMutation, useQueryClient } from '@tanstack/react-query';
import { apiRequest } from '@/lib/queryClient';
import type { SuperAdmin } from '@shared/schema';

interface SuperAdminAuthResponse {
  superAdmin: SuperAdmin;
  message: string;
}

interface SuperAdminSignupData {
  firstName: string;
  lastName: string;
  email: string;
  password: string;
  ownerCode: string;
  acceptTerms: boolean;
}

interface SuperAdminSigninData {
  email: string;
  password: string;
  twoFactorCode?: string;
  rememberMe?: boolean;
}

interface SuperAdminVerify2FAData {
  email: string;
  verificationCode: string;
}

export function useSuperAdminProfile() {
  return useQuery({
    queryKey: ['/api/super-admin/profile'],
    retry: false,
    staleTime: 0,
    gcTime: 0
  });
}

export function useSuperAdminAuth() {
  const { data: superAdmin, isLoading, error } = useSuperAdminProfile();
  const queryClient = useQueryClient();

  const signIn = useMutation({
    mutationFn: async (data: SuperAdminSigninData) => {
      const response = await apiRequest('POST', '/api/super-admin/signin', data);
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/super-admin/profile'] });
    }
  });

  return {
    superAdmin: superAdmin as SuperAdmin | undefined,
    isLoading,
    isAuthenticated: !!superAdmin && !error,
    error,
    signIn
  };
}

export function useSuperAdminSignup() {
  const queryClient = useQueryClient();

  return useMutation({
    mutationFn: async (data: SuperAdminSignupData) => {
      const response = await apiRequest('POST', '/api/super-admin/signup', data);
      return response.json();
    },
    onSuccess: () => {
      // Invalidate the profile query to refetch user data
      queryClient.invalidateQueries({ queryKey: ['/api/super-admin/profile'] });
    }
  });
}

export function useSuperAdminVerify2FA() {
  const queryClient = useQueryClient();

  return useMutation({
    mutationFn: async (data: SuperAdminVerify2FAData) => {
      const response = await apiRequest('POST', '/api/super-admin/verify-signup-2fa', data);
      return response.json();
    },
    onSuccess: () => {
      // Invalidate the profile query to refetch user data
      queryClient.invalidateQueries({ queryKey: ['/api/super-admin/profile'] });
    }
  });
}

export function useSuperAdminSignin() {
  const queryClient = useQueryClient();

  return useMutation({
    mutationFn: async (data: SuperAdminSigninData): Promise<SuperAdminAuthResponse> => {
      const response = await apiRequest('POST', '/api/super-admin/signin', data);
      return response.json();
    },
    onSuccess: () => {
      // Invalidate the profile query to refetch user data
      queryClient.invalidateQueries({ queryKey: ['/api/super-admin/profile'] });
    }
  });
}

export function useSuperAdminLogout() {
  const queryClient = useQueryClient();

  return useMutation({
    mutationFn: async () => {
      const response = await apiRequest('POST', '/api/super-admin/logout');
      return response.json();
    },
    onSuccess: () => {
      // Clear all cached data on logout
      queryClient.clear();
      // Specifically invalidate profile query
      queryClient.invalidateQueries({ queryKey: ['/api/super-admin/profile'] });
    }
  });
}

// Helper function to check if user has super admin access
export function useIsSuperAdmin() {
  const { superAdmin, isAuthenticated } = useSuperAdminAuth();

  return {
    isSuperAdmin: isAuthenticated && superAdmin?.role === 'super_admin',
    superAdmin,
    isAuthenticated
  };
}

