import React, { useState } from 'react';
import { motion } from 'framer-motion';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { useForm } from 'react-hook-form';
import { zodResolver } from '@hookform/resolvers/zod';
import { Form, FormControl, FormField, FormItem, FormLabel, FormMessage } from '@/components/ui/form';
import { useMutation } from '@tanstack/react-query';
import { apiRequest } from '@/lib/queryClient';
import { useToast } from '@/hooks/use-toast';
import { useLocation } from 'wouter';
import { useAdminAuth } from '@/hooks/useAdminAuth';
import { z } from 'zod';
import { Eye, EyeOff, Shield, Lock, Mail, LogIn, ArrowRight } from 'lucide-react';

const adminSignInSchema = z.object({
  email: z.string().email('Please enter a valid email address'),
  password: z.string().min(1, 'Password is required'),
  twoFactorCode: z.string().optional(),
  rememberMe: z.boolean().optional()
});

type AdminSignInForm = z.infer<typeof adminSignInSchema>;

export default function AdminSignIn() {
  const [, navigate] = useLocation();
  const { toast } = useToast();
  const [showPassword, setShowPassword] = useState(false);
  const [requiresTwoFactor, setRequiresTwoFactor] = useState(false);
  const { signIn } = useAdminAuth();

  const form = useForm<AdminSignInForm>({
    resolver: zodResolver(adminSignInSchema),
    defaultValues: {
      email: '',
      password: '',
      twoFactorCode: '',
      rememberMe: false
    }
  });

  const signInMutation = useMutation({
    mutationFn: async (data: AdminSignInForm) => {
      const response = await apiRequest('POST', '/api/admin/signin', {
        email: data.email,
        password: data.password,
        twoFactorCode: data.twoFactorCode,
        rememberMe: data.rememberMe
      });
      return response.json();
    },
    onSuccess: (data) => {
      if (data.requiresTwoFactor) {
        setRequiresTwoFactor(true);
        toast({
          title: "2FA Required",
          description: "Please enter your 6-digit authentication code from Google Authenticator.",
        });
      } else {
        // Store admin auth data for session management
        const adminAuth = {
          admin: data.admin,
          token: data.token || 'authenticated', // Session-based auth
          timestamp: Date.now()
        };
        localStorage.setItem('adminAuth', JSON.stringify(adminAuth));

        toast({
          title: "Welcome Back",
          description: "Successfully signed in to ChurPay Admin Dashboard.",
        });
        navigate('/admin/dashboard');
      }
    },
    onError: (error: any) => {
      toast({
        title: "Sign In Failed", 
        description: error.message || "Invalid credentials or 2FA code. Please try again.",
        variant: "destructive",
      });
    },
  });

  const onSubmit = (data: AdminSignInForm) => {
    signInMutation.mutate(data);
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-gray-50 via-gray-100 to-gray-200 flex items-center justify-center p-4">
      <motion.div
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.5 }}
        className="w-full max-w-md"
      >
        {/* Header */}
        <div className="text-center mb-8">
          <div className="inline-flex items-center justify-center w-16 h-16 bg-gradient-to-r from-purple-600 to-purple-700 rounded-xl mb-4">
            <Shield className="h-8 w-8 text-white" />
          </div>
          <h1 className="text-3xl font-bold text-gray-900 mb-2">ChurPay Admin</h1>
          <p className="text-gray-600">Sign in to your administrator account</p>
        </div>

        <Card className="shadow-xl border-0 bg-white/95 backdrop-blur-sm">
          <CardHeader className="text-center pb-4">
            <CardTitle className="text-xl font-semibold text-gray-900">
              Administrator Sign In
            </CardTitle>
          </CardHeader>
          <CardContent className="space-y-6">
            <Form {...form}>
              <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-4">
                {/* Email */}
                <FormField
                  control={form.control}
                  name="email"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel className="text-sm font-medium text-gray-700">Email Address</FormLabel>
                      <FormControl>
                        <div className="relative">
                          <Mail className="absolute left-3 top-3 h-4 w-4 text-gray-400" />
                          <Input
                            {...field}
                            type="email"
                            className="pl-10 h-11 border-gray-200 focus:border-purple-500 focus:ring-purple-500"
                            placeholder="admin@church.org"
                          />
                        </div>
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />

                {/* Password */}
                <FormField
                  control={form.control}
                  name="password"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel className="text-sm font-medium text-gray-700">Password</FormLabel>
                      <FormControl>
                        <div className="relative">
                          <Lock className="absolute left-3 top-3 h-4 w-4 text-gray-400" />
                          <Input
                            {...field}
                            type={showPassword ? "text" : "password"}
                            className="pl-10 pr-10 h-11 border-gray-200 focus:border-purple-500 focus:ring-purple-500"
                            placeholder="Enter password"
                          />
                          <button
                            type="button"
                            onClick={() => setShowPassword(!showPassword)}
                            className="absolute right-3 top-3 text-gray-400 hover:text-gray-600"
                          >
                            {showPassword ? <EyeOff className="h-4 w-4" /> : <Eye className="h-4 w-4" />}
                          </button>
                        </div>
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />

                {/* Two-Factor Authentication Code */}
                {requiresTwoFactor && (
                  <FormField
                    control={form.control}
                    name="twoFactorCode"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel className="text-sm font-medium text-gray-700">
                          Authentication Code
                        </FormLabel>
                        <FormControl>
                          <div className="relative">
                            <Shield className="absolute left-3 top-3 h-4 w-4 text-gray-400" />
                            <Input
                              {...field}
                              type="text"
                              placeholder="Enter 6-digit code"
                              maxLength={6}
                              className="pl-10 h-11 border-gray-200 focus:border-purple-500 focus:ring-purple-500 text-center tracking-wider"
                              onChange={(e) => field.onChange(e.target.value.replace(/\D/g, ''))}
                            />
                          </div>
                        </FormControl>
                        <div className="text-xs text-gray-500">
                          Enter the 6-digit code from your Google Authenticator app
                        </div>
                        <FormMessage />
                      </FormItem>
                    )}
                  />
                )}

                {/* Remember Me & Forgot Password */}
                <div className="flex items-center justify-between">
                  <FormField
                    control={form.control}
                    name="rememberMe"
                    render={({ field }) => (
                      <FormItem className="flex flex-row items-center space-x-2 space-y-0">
                        <FormControl>
                          <input
                            type="checkbox"
                            checked={field.value}
                            onChange={field.onChange}
                            className="h-4 w-4 text-purple-600 focus:ring-purple-500 border-gray-300 rounded"
                          />
                        </FormControl>
                        <FormLabel className="text-sm text-gray-700 font-normal">
                          Remember me
                        </FormLabel>
                      </FormItem>
                    )}
                  />
                  <button
                    type="button"
                    onClick={() => navigate('/admin/forgot-password')}
                    className="text-sm text-purple-600 hover:text-purple-700 font-medium"
                  >
                    Forgot password?
                  </button>
                </div>

                <Button 
                  type="submit" 
                  className="w-full h-11 bg-gradient-to-r from-purple-600 to-purple-700 hover:from-purple-700 hover:to-purple-800 text-white font-medium"
                  disabled={signInMutation.isPending}
                >
                  {signInMutation.isPending ? (
                    <div className="flex items-center space-x-2">
                      <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-white"></div>
                      <span>Signing In...</span>
                    </div>
                  ) : (
                    <div className="flex items-center space-x-2">
                      <LogIn className="h-4 w-4" />
                      <span>Sign In</span>
                      <ArrowRight className="h-4 w-4" />
                    </div>
                  )}
                </Button>
              </form>
            </Form>

            <div className="text-center pt-4 border-t border-gray-100">
              <p className="text-sm text-gray-600">
                Need an admin account?{' '}
                <button
                  onClick={() => navigate('/admin/signup')}
                  className="text-purple-600 hover:text-purple-700 font-medium"
                >
                  Sign Up
                </button>
              </p>
            </div>
          </CardContent>
        </Card>

        {/* Quick Access */}
        <div className="mt-6 grid grid-cols-2 gap-4">
          <Card className="p-4 bg-white/80 hover:bg-white/90 transition-colors cursor-pointer" onClick={() => navigate('/')}>
            <div className="text-center">
              <div className="w-8 h-8 bg-blue-100 rounded-lg flex items-center justify-center mx-auto mb-2">
                <ArrowRight className="h-4 w-4 text-blue-600 rotate-180" />
              </div>
              <p className="text-sm font-medium text-gray-900">Back to Home</p>
              <p className="text-xs text-gray-600">Public site</p>
            </div>
          </Card>

          <Card className="p-4 bg-white/80 hover:bg-white/90 transition-colors cursor-pointer" onClick={() => navigate('/super-admin')}>
            <div className="text-center">
              <div className="w-8 h-8 bg-purple-100 rounded-lg flex items-center justify-center mx-auto mb-2">
                <Shield className="h-4 w-4 text-purple-600" />
              </div>
              <p className="text-sm font-medium text-gray-900">Super Admin</p>
              <p className="text-xs text-gray-600">System control</p>
            </div>
          </Card>
        </div>

        {/* Security Notice */}
        <div className="mt-6 p-4 bg-purple-50 border border-purple-200 rounded-lg">
          <div className="flex items-start space-x-3">
            <Shield className="h-5 w-5 text-purple-600 mt-0.5" />
            <div className="text-sm">
              <p className="font-medium text-purple-900">Secure Admin Access</p>
              <p className="text-purple-700 mt-1">
                Administrator accounts provide full system access with enterprise-grade security.
              </p>
            </div>
          </div>
        </div>
      </motion.div>
    </div>
  );
}

