import { useState } from 'react';
import { useQuery } from '@tanstack/react-query';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Badge } from '@/components/ui/badge';
import { Input } from '@/components/ui/input';
import { 
  Building2, Users, Wallet, TrendingUp, 
  Activity, CreditCard, Banknote, Shield,
  Search, Bell, Settings, ChevronRight,
  ArrowUpRight, ArrowDownRight, Clock, Plus,
  Heart, Church, Target, DollarSign, 
  Receipt, BarChart3, FileText, UserPlus,
  Download, Eye, Calendar, MapPin
} from 'lucide-react';
import { ChurchPayoutModal } from '@/components/ChurchPayoutModal';
import { ChurchMemberModal } from '@/components/ChurchMemberModal';
import { ChurchProjectModal } from '@/components/ChurchProjectModal';
import { ChurchProfileModal } from '@/components/ChurchProfileModal';
import { NotificationTest } from '@/components/NotificationTest';
import ChurPayLogo from "@assets/Churpay Logo tuesd_1754519336175.png";

interface ChurchData {
  id: string;
  name: string;
  denomination: string;
  registrationNumber: string;
  taxNumber: string;
  yearEstablished: string;

  // Contact Information
  contactEmail: string;
  contactPhone: string;
  alternativePhone?: string;
  website?: string;

  // Physical Address
  address: string;
  city: string;
  province: string;
  postalCode: string;
  country: string;

  // Banking Information
  bankName: string;
  accountNumber: string;
  branchCode: string;
  accountHolder: string;
  accountType: string;

  // Church Details
  description: string;
  memberCount: number;
  servicesTimes: string;
  leadPastor: string;
  logoUrl?: string;

  // Administrative Contact
  adminFirstName: string;
  adminLastName: string;
  adminEmail: string;
  adminPhone: string;
  adminPosition: string;

  // Status and financial data
  status: string;
  totalRevenue: string;
  monthlyRevenue: string;
  pendingPayouts: string;
  availableBalance: string;
  registrationDate: string;
}

interface MemberData {
  id: string;
  firstName: string;
  lastName: string;
  email: string;
  phone: string;
  membershipType: string;
  totalDonated: string;
  lastDonation: string;
  joinDate: string;
  status: string;
}

interface TransactionData {
  id: string;
  memberName: string;
  amount: string;
  type: 'tithe' | 'donation' | 'project' | 'offering';
  projectTitle?: string;
  createdAt: string;
  status: string;
  paymentMethod: string;
}

interface ProjectData {
  id: string;
  title: string;
  description: string;
  targetAmount: string;
  currentAmount: string;
  progress: number;
  donorCount: number;
  status: string;
  startDate: string;
  endDate: string;
}

export default function ProfessionalChurchDashboard() {
  const [showMemberModal, setShowMemberModal] = useState(false);
  const [showPayoutModal, setShowPayoutModal] = useState(false);
  const [showProjectModal, setShowProjectModal] = useState(false);
  const [showProfileModal, setShowProfileModal] = useState(false);
  const [searchQuery, setSearchQuery] = useState('');

  // Data queries
  const { data: churchData } = useQuery<ChurchData>({
    queryKey: ['/api/church/profile']
  });

