                      <p className="text-sm text-purple-200">Members</p>
                    </div>
                    <div>
                      <p className="text-2xl font-bold text-white">R{churchStats?.monthlyRevenue}</p>
                      <p className="text-sm text-purple-200">This Month</p>
                    </div>
                  </div>
                </div>
              </CardContent>
            </Card>

            {/* Top Donors */}
