                        </div>
                      </div>
                    </div>

                    <div className="space-y-3">
                      <Button
                        onClick={() => setLocation("/church-dashboard")}
                        className="bg-gradient-to-r from-purple-500 to-blue-600 text-white px-8"
                      >
                        Access Dashboard
                      </Button>
                      <div>
                        <Button
                          variant="ghost"
                          onClick={() => setLocation("/")}
                          className="text-gray-600"
                        >
                          Return to Home
                        </Button>
                      </div>
                    </div>
                  </div>
                )}

                {/* Navigation Buttons - Hide on completion step */}
                {currentStep !== 7 && (
                <div className="flex justify-between">
                  {currentStep > 1 && (
                    <Button
                      type="button"
                      variant="outline"
                      onClick={prevStep}
                    >
                      Previous
                    </Button>
                  )}
                  {currentStep < steps.length ? (
                    <Button
                      type="button"
                      onClick={nextStep}
                      className="ml-auto bg-purple-600 hover:bg-purple-700 text-white"
                    >
                      Next
                    </Button>
                  ) : (
                    <Button
                      type="submit"
